 return gr.sys_id + '';

 return;
 }


 function commentOnTask(taskGr, msg) {
 var comment = msg;

 var existingComments = taskGr.comments.getJournalEntry(-1).split('\n\n');
 for (var i = 0; i < existingComments.length; i++) {
			var existingComment = existingComments[i];

			existingCommentMod = existingComment.replace(/\n/g, '');
			commentMod = comment.replace(/\n/g,'');

 if (existingCommentMod.indexOf(commentMod) >= 0) {
 return;
 }
 }

 taskGr.comments = comment;
 }

